const lead = $input.first().json;

// Get enrichment data from merged sources
const enrichment = {
  company: lead.enrichment_company || {},
  linkedin: lead.enrichment_linkedin || {},
  website: lead.enrichment_website || {},
  historical: Array.isArray(lead.enrichment_historical) ? lead.enrichment_historical : []
};

// Helper functions
function extractRevenueFromWebsite(website) {
  if (!website || !website.description) return null;

  const revenuePatterns = [
    /\$(\d+)M revenue/i,
    /revenue of \$(\d+)M/i,
    /\$(\d+) million in sales/i
  ];

  for (const pattern of revenuePatterns) {
    const match = website.description.match(pattern);
    if (match) return `$${match[1]}M`;
  }

  return null;
}

function inferSeniority(title) {
  if (!title) return 'Individual Contributor';
  const titleLower = title.toLowerCase();

  if (titleLower.includes('ceo') || titleLower.includes('president') || titleLower.includes('founder')) return 'C-Level';
  if (titleLower.includes('cto') || titleLower.includes('cfo') || titleLower.includes('coo')) return 'C-Level';
  if (titleLower.includes('vp') || titleLower.includes('vice president')) return 'VP';
  if (titleLower.includes('director')) return 'Director';
  if (titleLower.includes('manager')) return 'Manager';
  if (titleLower.includes('senior') || titleLower.includes('lead')) return 'Senior';

  return 'Individual Contributor';
}

function calculateEnrichmentScore(enrichment) {
  let score = 0;
  if (enrichment.company && Object.keys(enrichment.company).length > 0) score += 25;
  if (enrichment.linkedin && Object.keys(enrichment.linkedin).length > 0) score += 25;
  if (enrichment.website && Object.keys(enrichment.website).length > 0) score += 25;
  if (enrichment.historical && enrichment.historical.length > 0) score += 25;
  return score;
}

// Merge enrichment data with preference order
const enriched = {
  ...lead,

  // Company data (prefer API > website)
  revenue: enrichment.company.revenue || extractRevenueFromWebsite(enrichment.website),
  employees: enrichment.company.employees || enrichment.linkedin.company_size,
  industry: enrichment.company.industry || lead.industry,
  tech_stack: enrichment.company.tech_stack || [],
  founded_year: enrichment.company.founded,
  funding: enrichment.company.funding,

  // Contact data (prefer LinkedIn > form)
  verified_title: enrichment.linkedin.title || lead.title,
  seniority: enrichment.linkedin.seniority || inferSeniority(lead.title),
  years_experience: enrichment.linkedin.years_experience,

  // Engagement signals
  has_pricing_page: !!enrichment.website.pricing_page,
  website_keywords: enrichment.website.keywords || [],

  // Historical context
  previous_submissions: enrichment.historical.length || 0,
  last_contact_date: enrichment.historical[0]?.created_at,
  past_scores: enrichment.historical.map(h => h.score).filter(Boolean) || [],

  // Enrichment metadata
  enrichment_sources: [
    enrichment.company ? 'clearbit' : null,
    enrichment.linkedin ? 'linkedin' : null,
    enrichment.website ? 'website' : null,
    enrichment.historical.length > 0 ? 'crm' : null
  ].filter(Boolean),
  enrichment_score: calculateEnrichmentScore(enrichment),
  enriched_at: new Date().toISOString()
};

return [{ json: enriched }];

