const lead = $input.first().json;

// Get component scores from AI nodes
const scores = {
  demographics: $('scoreDemographics').first().json.score || 0,
  engagement: $('scoreEngagement').first().json.score || 0,
  intent: $('scoreIntent').first().json.score || 0
};

// Weighted calculation
const weights = {
  demographics: 0.25,  // 25% - baseline fit
  engagement: 0.35,    // 35% - behavior matters most
  intent: 0.40         // 40% - buying intent is critical
};

const finalScore = Math.round(
  scores.demographics * weights.demographics +
  scores.engagement * weights.engagement +
  scores.intent * weights.intent
);

// Determine grade and priority
let grade, label, priority, color;
if (finalScore >= 85) {
  grade = 'A+';
  label = 'Hot Lead';
  priority = 'Immediate';
  color = '#FF0000';
} else if (finalScore >= 70) {
  grade = 'A';
  label = 'Qualified Lead';
  priority = 'High';
  color = '#FF6600';
} else if (finalScore >= 55) {
  grade = 'B';
  label = 'Warm Lead';
  priority = 'Medium';
  color = '#FFAA00';
} else if (finalScore >= 40) {
  grade = 'C';
  label = 'Cool Lead';
  priority = 'Low';
  color = '#FFDD00';
} else {
  grade = 'D';
  label = 'Cold Lead';
  priority = 'Nurture';
  color = '#CCCCCC';
}

// Generate recommendations
function generateRecommendations(scores, finalScore) {
  const recs = [];

  if (scores.engagement > 70 && scores.intent < 40) {
    recs.push({
      type: 'nurture',
      action: 'Send educational content to build intent',
      reason: 'High engagement but unclear buying intent'
    });
  }

  if (scores.intent > 70 && scores.engagement < 40) {
    recs.push({
      type: 'engage',
      action: 'Immediate outreach call recommended',
      reason: 'Strong buying signals but low engagement'
    });
  }

  if (scores.demographics >= 80) {
    recs.push({
      type: 'personalize',
      action: 'Executive-level outreach with case studies',
      reason: 'Perfect ICP match'
    });
  }

  if (finalScore >= 70) {
    recs.push({
      type: 'follow_up',
      action: 'Follow up within 24 hours',
      reason: 'High-quality lead with strong signals'
    });
  }

  return recs;
}

const recommendations = generateRecommendations(scores, finalScore);

return [{
  json: {
    ...lead,
    scoring: {
      final_score: finalScore,
      grade: grade,
      label: label,
      priority: priority,
      color: color,
      component_scores: scores,
      weights: weights,
      recommendations: recommendations,
      scored_at: new Date().toISOString()
    }
  }
}];

