const lead = $input.first().json;

// Token cost per million tokens
const costs = {
  'gemini-flash': 0,
  'gpt-4o-mini': 0.15 / 1_000_000,
  'claude-sonnet': 3 / 1_000_000,
  'gpt-4o': 2.5 / 1_000_000
};

// Estimated token usage
const usage = {
  demographics: { model: 'gemini-flash', tokens: 500 },
  engagement: { model: 'gpt-4o-mini', tokens: 800 },
  intent: { model: 'claude-sonnet', tokens: 1500 },
  judge: { model: 'gpt-4o', tokens: 600 }
};

// Calculate total cost
const totalCost = Object.values(usage).reduce((sum, item) => {
  return sum + (costs[item.model] * item.tokens);
}, 0);

const aiCost = {
  total_usd: totalCost.toFixed(6),
  breakdown: usage,
  cost_per_lead: totalCost.toFixed(6),
  estimated_monthly: (totalCost * 500 * 30).toFixed(2),
  ai_skipped: lead.scoring?.ai_skipped || false
};

return [{
  json: {
    ...lead,
    ai_cost: aiCost
  }
}];

